import React,{useEffect,useState} from 'react'
import './mainPage.css'
import { useLocalStorage } from '../../hooks/useLocalStorage'

const MainPage  = ()=>{
    const [loggedIn,setLoggedIn] = useState(true)
    const [token,setToken] = useLocalStorage("token","")
    const [notes,setNotes] = useState([])

    const getData = async ()=>{
        if (token!=""){
        try{
            const response = await fetch('http://127.0.0.1:8000/api/notes/get/', {
                    method: 'GET',
                    headers: {
                        Accept: 'application/json',
                        'Content-Type': 'application/json',
                        'Authorization':'Token ' + token
                    },

                })
                const json = response.json()
                console.log(json)
                setNotes(json.data)
                setLoggedIn(true)
                return json.data
        }catch(err){
            console.log(err)
        }
    }else{
        setLoggedIn(false)
    }
    }
    useEffect(()=>{
        getData()
    },[])

    const noteItems = notes.map((item)=>{
        <div key={item.id}>
            <h2>{item.name}</h2>
            <a>{item.date}</a>
        </div>
    }) 
    console.log(token)
    return(
        <div id="container">

            <h1>Welcome to Main Page !</h1>
            <div id="content">
                {loggedIn 
                   ? <div>
                        <h2>My Notes </h2>
                        <div id="items">
                            {
                        notes.map((item)=>{
                            <div key={item.id}>
                                <h2>{item.name}</h2>
                                <a>{item.date}</a>
                            </div>
                        })
                    }
                        </div>
                    </div>
                    :  
                    <h2>You are not logged in</h2>
                }
            </div>
        </div>
    )
}

export default MainPage
